import React, { useContext, useState, useRef, useEffect } from 'react';
import { AuthContext } from './AuthContext';
import LoginRegister from './LoginRegister';
import Habits from './Habits';
import AboutMe from './AboutMe';
import ContactMe from './ContactMe';
import Hero from './Hero';
import Tutorial from './Tutorial';
function App() {
  const { token } = useContext(AuthContext);
  const [page, setPage] = useState('home');
  const [dark, setDark] = useState(false);
  const [authInitial, setAuthInitial] = useState(null); // 'register' | 'login' | null
  const [showTutorial, setShowTutorial] = useState(false);
  const [showGetStartedFlag, setShowGetStartedFlag] = useState(false);
  const [showBackToTop, setShowBackToTop] = useState(true); // Force to true for testing
  const contactRef = useRef(null);

  React.useEffect(() => {
    document.body.classList.toggle('dark', dark);
  }, [dark]);

  // Handle scroll detection for Back to Top button
  useEffect(() => {
    console.log('Setting up scroll listener');
    let scrollCount = 0;
    
    const handleScroll = () => {
      scrollCount++;
      const scrollY = window.scrollY || window.pageYOffset || document.documentElement.scrollTop || 0;
      const shouldShow = scrollY > 50;
      console.log(`Scroll event #${scrollCount}:`, {
        scrollY,
        shouldShow,
        currentState: showBackToTop
      });
      setShowBackToTop(shouldShow);
    };

    window.addEventListener('scroll', handleScroll);
    document.addEventListener('scroll', handleScroll);
    
    // Force initial check
    setTimeout(handleScroll, 100);
    
    return () => {
      console.log('Removing scroll listeners');
      window.removeEventListener('scroll', handleScroll);
      document.removeEventListener('scroll', handleScroll);
    };
  }, [showBackToTop]);

  // Reflect localStorage flag into React state and auto-open tutorial after first login
  React.useEffect(() => {
    const shouldShow = !!(token && localStorage.getItem('showGetStarted') === 'true' && localStorage.getItem('tutorialSeen') !== 'true');
    setShowGetStartedFlag(shouldShow);
    
    // Check for immediate tutorial trigger (set during registration)
    const autoShow = localStorage.getItem('autoShowTutorial') === 'true';
    if (token && (shouldShow || autoShow)) {
      // Small delay to ensure Habits component renders first
      setTimeout(() => {
        setShowTutorial(true);
        localStorage.removeItem('autoShowTutorial');
      }, 600);
    }
  }, [token]);

  const handleScrollToContact = () => {
    if (contactRef.current) {
      contactRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleBackToTop = () => {
    // Multiple scroll methods to ensure compatibility
    window.scrollTo({
      top: 0,
      left: 0,
      behavior: 'smooth'
    });
    
    // Fallback methods
    document.documentElement.scrollTop = 0;
    document.body.scrollTop = 0;
  };

  return (
    <div id="layout">
      <header className="site-header">
        <div className="header-logo">üå±</div>
        <div className="header-title">Habit Tracker SaaS</div>
        <div className="header-subtitle">Track your habits, grow your life</div>
        <button className="header-contact-btn" onClick={handleScrollToContact}>Contact Me</button>
      </header>
      <Hero
        contactRef={contactRef}
        setPage={setPage}
        token={token}
        setAuthInitial={setAuthInitial}
        showGetStarted={showGetStartedFlag}
        openTutorial={() => setShowTutorial(true)}
      />
      <div className="main-wrapper">
        <aside className="sidebar">
          <div className="sidebar-title">Navigation</div>
          <button className={`sidebar-link${page==='home'?' active':''}`} onClick={() => setPage('home')}>Home</button>
          <button className={`sidebar-link${page==='about'?' active':''}`} onClick={() => setPage('about')}>About Me</button>
          <button className="sidebar-dark-toggle" onClick={() => setDark(d => !d)}>
            {dark ? 'Light Mode' : 'Dark Mode'}
          </button>
        </aside>
        <main className="main-content">
          {page === 'about' ? <AboutMe /> : (token ? <Habits /> : <LoginRegister initial={authInitial} />)}
          
          {/* Guaranteed scrollable content for testing */}
          <div style={{
            height: '1200px',
            background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4)',
            margin: '20px 0',
            borderRadius: '16px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '24px',
            color: 'white',
            textShadow: '2px 2px 4px rgba(0,0,0,0.5)',
            flexDirection: 'column',
            gap: '20px'
          }}>
            <h2>üéØ SCROLL TEST AREA</h2>
            <p>This is a tall section to enable scrolling</p>
            <p>Scroll down to see the Back to Top button appear!</p>
            <p>Button should show when Scroll Y > 30</p>
          </div>
          
          {/* ContactMe at end of landing page */}
          <div ref={contactRef} id="contact-section">
            <ContactMe />
          </div>
        </main>
        {/* Render tutorial at root level so it truly pops up above everything */}
        <Tutorial visible={showTutorial} onClose={() => setShowTutorial(false)} />
      </div>
      {/* Help button to re-open tutorial (always visible when logged in) */}
      {token && !showTutorial && (
        <button
          className="tutorial-help-btn"
          onClick={() => setShowTutorial(true)}
          title="Show tutorial"
          aria-label="Show tutorial"
        >
          ?
        </button>
      )}
      <footer className="site-footer">
        &copy; {new Date().getFullYear()} Habit Tracker SaaS. All rights reserved. | Created by Mamdouh
      </footer>
      
      {/* Back to Top Button */}
      {true && (
        <button
          className="back-to-top-btn"
          onClick={handleBackToTop}
          aria-label="Back to top"
          title="Back to top"
        >
          <span className="back-to-top-icon">‚Üë</span>
        </button>
      )}
      
      {/* Debug indicator */}
      <div style={{
        position: 'fixed',
        top: '10px',
        right: '10px',
        background: 'rgba(255,0,0,0.9)',
        color: 'white',
        padding: '15px',
        borderRadius: '8px',
        fontSize: '14px',
        zIndex: 10000,
        border: '2px solid white'
      }}>
        Scroll Y: {typeof window !== 'undefined' ? (window.pageYOffset || document.documentElement.scrollTop || 0) : 0}<br/>
        Should Show: {(typeof window !== 'undefined' ? (window.pageYOffset || document.documentElement.scrollTop || 0) : 0) > 100 ? 'TRUE' : 'FALSE'}<br/>
        Button State: {showBackToTop ? 'VISIBLE ‚úÖ' : 'HIDDEN ‚ùå'}
      </div>
    </div>
  );
}

export default App;
