import React, { useState } from 'react';

export default function ContactMe() {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [error, setError] = useState('');

  const handleSend = async (e) => {
    e.preventDefault();
    setError('');
    if (!message) {
      setError('Message cannot be empty.');
      return;
    }
    // Simulate sending email (in real app, use backend or email API)
    setSent(true);
  };

  return (
    <div className="contact-box">
      <h2>Contact Me</h2>
      <form onSubmit={handleSend}>
        <input
          className="contact-input"
          type="email"
          placeholder="Your email (optional)"
          value={email}
          onChange={e => setEmail(e.target.value)}
        />
        <textarea
          className="contact-textarea"
          placeholder="Your message"
          value={message}
          onChange={e => setMessage(e.target.value)}
          rows={5}
        />
        <button className="contact-send-btn" type="submit">Send</button>
      </form>
      {sent && <div className="contact-success">Message sent! I'll reply to mamdouhaiesh76@gmail.com.</div>}
      {error && <div className="error">{error}</div>}
      <div className="contact-info">Developer email: <a href="mailto:mamdouhaiesh76@gmail.com">mamdouhaiesh76@gmail.com</a></div>
    </div>
  );
}
