import React, { useState, useEffect, useRef, useCallback } from 'react';

export default function Tutorial({ visible, onClose }) {
  const [step, setStep] = useState(0);
  const [highlight, setHighlight] = useState(null); // {top,left,width,height}
  const retryCountRef = useRef(0);

  // Steps: user must perform each action to advance
  const steps = [
    {
      title: 'Type a Habit',
      body: 'Type a habit in the input box below. Try "Brush your teeth" as a suggestion!',
      selector: '.add-habit-input',
      waitFor: 'input', // advance when input has value
      placement: 'bottom'
    },
    {
      title: 'Add the Habit',
      body: 'Click the "Add" button to save your habit.',
      selector: '.add-habit-btn',
      waitFor: 'habitAdded', // advance when a new habit appears in the list
      placement: 'bottom'
    },
    {
      title: 'Mark as Done',
      body: 'Click "Mark as done" to complete the habit for today.',
      selector: '.habit-complete-btn',
      waitFor: 'habitCompleted', // advance when .habit-done appears
      placement: 'left'
    },
    {
      title: 'Delete the Habit',
      body: 'Click "Delete" to remove the habit.',
      selector: '.habit-delete-btn',
      waitFor: 'habitDeleted', // advance when habit is removed
      placement: 'left'
    }
  ];

  const finish = useCallback(() => {
    try {
      localStorage.setItem('tutorialSeen', 'true');
      localStorage.removeItem('showGetStarted');
    } catch (e) {}
    onClose();
  }, [onClose]);

  // Just recalculate highlight position (used for scroll/resize)
  const recalculateHighlight = useCallback((currentStep) => {
    const s = steps[currentStep];
    if (!s?.selector) return;
    const el = document.querySelector(s.selector);
    if (!el) return;
    const rect = el.getBoundingClientRect();
    setHighlight({
      top: rect.top,
      left: rect.left,
      width: rect.width,
      height: rect.height,
      placement: s.placement || 'bottom'
    });
  }, [steps]);

  // Position highlight box around the target element (with retry and scroll)
  const updateHighlight = useCallback((currentStep) => {
    const s = steps[currentStep];
    if (!s?.selector) {
      setHighlight(null);
      return;
    }
    const el = document.querySelector(s.selector);
    if (!el) {
      // Retry up to 10 times with 300ms delay
      if (retryCountRef.current < 10) {
        retryCountRef.current++;
        setTimeout(() => updateHighlight(currentStep), 300);
      }
      return;
    }
    retryCountRef.current = 0;
    
    // First scroll into view, then calculate position after scroll settles
    el.scrollIntoView({ behavior: 'smooth', block: 'center', inline: 'center' });
    
    // Wait for scroll to complete before calculating position
    setTimeout(() => {
      const rect = el.getBoundingClientRect();
      setHighlight({
        top: rect.top,
        left: rect.left,
        width: rect.width,
        height: rect.height,
        placement: s.placement || 'bottom'
      });
    }, 400);
  }, [steps]);

  // Watch for user completing the current step's action
  useEffect(() => {
    if (!visible) return;

    const s = steps[step];
    if (!s) return;

    let intervalId = null;
    let previousHabitCount = document.querySelectorAll('ul li').length;

    const checkCondition = () => {
      switch (s.waitFor) {
        case 'input': {
          const input = document.querySelector('.add-habit-input');
          if (input && input.value.trim().length > 0) {
            setStep(prev => prev + 1);
          }
          break;
        }
        case 'habitAdded': {
          const currentCount = document.querySelectorAll('ul li').length;
          if (currentCount > previousHabitCount) {
            setStep(prev => prev + 1);
          }
          break;
        }
        case 'habitCompleted': {
          const doneIndicator = document.querySelector('.habit-done');
          if (doneIndicator) {
            setStep(prev => prev + 1);
          }
          break;
        }
        case 'habitDeleted': {
          const currentCount = document.querySelectorAll('ul li').length;
          if (currentCount < previousHabitCount || currentCount === 0) {
            finish();
          }
          break;
        }
        default:
          break;
      }
    };

    // Poll for condition
    intervalId = setInterval(checkCondition, 200);

    return () => {
      if (intervalId) clearInterval(intervalId);
    };
  }, [visible, step, finish, steps]);

  // Update highlight when step changes
  useEffect(() => {
    if (visible) {
      retryCountRef.current = 0;
      updateHighlight(step);
      
      // Re-check highlight position on scroll/resize (without triggering scroll)
      const handleUpdate = () => recalculateHighlight(step);
      window.addEventListener('scroll', handleUpdate);
      window.addEventListener('resize', handleUpdate);
      return () => {
        window.removeEventListener('scroll', handleUpdate);
        window.removeEventListener('resize', handleUpdate);
      };
    }
  }, [visible, step, updateHighlight, recalculateHighlight]);

  // Reset step when opening
  useEffect(() => {
    if (visible) {
      setStep(0);
      setHighlight(null);
      retryCountRef.current = 0;
    }
  }, [visible]);

  if (!visible) return null;

  const s = steps[step];

  // Calculate card position
  let cardStyle = {
    position: 'fixed',
    zIndex: 100001
  };
  
  if (highlight) {
    if (highlight.placement === 'bottom') {
      cardStyle.top = highlight.top + highlight.height + 24;
      cardStyle.left = Math.max(16, Math.min(highlight.left, window.innerWidth - 360));
    } else if (highlight.placement === 'left') {
      cardStyle.top = highlight.top;
      cardStyle.left = Math.max(16, highlight.left - 360);
    } else {
      cardStyle.top = Math.max(16, highlight.top - 200);
      cardStyle.left = Math.max(16, Math.min(highlight.left, window.innerWidth - 360));
    }
  } else {
    // Center the card if no highlight yet
    cardStyle.top = '50%';
    cardStyle.left = '50%';
    cardStyle.transform = 'translate(-50%, -50%)';
  }

  return (
    <>
      {/* Light overlay that allows interaction */}
      <div className="tutorial-overlay-light" />

      {/* Highlight box around target element */}
      {highlight && (
        <div
          className="tutorial-highlight"
          style={{
            top: highlight.top - 6,
            left: highlight.left - 6,
            width: highlight.width + 12,
            height: highlight.height + 12
          }}
        />
      )}

      {/* Instruction card */}
      <div
        className="tutorial-card tutorial-card--floating"
        role="dialog"
        aria-modal="false"
        style={cardStyle}
      >
        <div className="tutorial-header">
          <div className="tutorial-step">Step {step + 1} / {steps.length}</div>
          <button className="tutorial-skip" onClick={finish}>Skip</button>
        </div>
        <h3 className="tutorial-title">{s.title}</h3>
        <p className="tutorial-body">{s.body}</p>
      </div>
    </>
  );
}
