import React, { useState, useEffect, useRef } from 'react';
import { setTutorialSeen, addHabit, getHabits } from './api';

// Helper: find element with retry
function findElement(selector, attempts = 12, interval = 120) {
  return new Promise(resolve => {
    let i = 0;
    const tick = () => {
      const el = document.querySelector(selector);
      if (el) return resolve(el);
      i += 1;
      if (i >= attempts) return resolve(null);
      setTimeout(tick, interval);
    };
    tick();
  });
}

export default function Tutorial({ visible, onClose, token, setPage }) {
  const [step, setStep] = useState(0);
  const [pointer, setPointer] = useState(null); // {top,left,width,height,placement}
  const [cardStyle, setCardStyle] = useState(null); // when present, position the tutorial card next to the target
  const finishBtnRef = useRef(null);
  const cardRef = useRef(null);

  const interactiveSteps = [
    {
      title: 'Welcome',
      body: 'This quick interactive tour shows how to add and complete habits.'
    },
    {
      title: 'Add a Habit',
      body: 'Type a short habit name in this field. (We will demo "Brush your teeth" for you.)',
      selector: '.add-habit-input',
      placement: 'right',
      page: 'home'
    },
    {
      title: 'Save the Habit',
      body: 'Click the Add button to save your habit. We will add it automatically for the demo.',
      selector: '.add-habit-btn',
      placement: 'bottom',
      page: 'home',
      autoFill: 'Brush your teeth'
    },
    {
      title: 'Mark Complete',
      body: 'Tap the mark-as-done button to record a completion for today. We will briefly show the effect.',
      selector: '.habit-complete-btn',
      placement: 'left',
      page: 'home',
      autoClick: true,
      demoName: 'Brush your teeth'
    },
    {
      title: 'Delete a Habit',
      body: 'You can remove habits using the delete button.',
      selector: '.habit-delete-btn',
      placement: 'left',
      page: 'home',
      demoName: 'Brush your teeth'
    }
  ];

  useEffect(() => {
    if (visible) {
      console.log('Tutorial: visible true — mounting tutorial overlay');
      setStep(0);
      setPointer(null);
      document.body.style.overflow = 'hidden';
      setTimeout(() => finishBtnRef.current?.focus(), 120);
      return () => { document.body.style.overflow = ''; };
    } else {
      console.log('Tutorial: visible false');
    }
  }, [visible]);

  // when step changes, if it has a selector, try to locate and position the pointer/highlight
  useEffect(() => {
    if (!visible) return;
    const s = interactiveSteps[step];
    let cancelled = false;

    (async () => {
      setPointer(null);
      if (s?.page && setPage) setPage(s.page);
      if (!s?.selector) return;

      // helper: find the button for a habit by name inside the list
      const findHabitButtonByName = async (selector, name, attempts = 8, interval = 150) => {
        for (let i = 0; i < attempts; i++) {
          const li = Array.from(document.querySelectorAll('li')).find(li => li.textContent && li.textContent.toLowerCase().includes(name.toLowerCase()));
          if (li) {
            const btn = li.querySelector(selector);
            if (btn) return btn;
          }
          await new Promise(r => setTimeout(r, interval));
        }
        return null;
      };

      let el = null;

      // if the step targets a habit action, prefer the demo habit by name and fall back to API if needed
      if ((s.selector === '.habit-complete-btn' || s.selector === '.habit-delete-btn') && s.demoName) {
        el = await findHabitButtonByName(s.selector, s.demoName);

        if (!el && token) {
          try {
            const existing = await getHabits(token);
            if (!existing.data.some(h => h.name === s.demoName)) {
              await addHabit(token, s.demoName);
            }
            await new Promise(r => setTimeout(r, 400));
            el = await findHabitButtonByName(s.selector, s.demoName);
          } catch (err) {
            /* ignore */
          }
        }

        if (!el) {
          // final fallback to any matching selector in the DOM
          el = await findElement(s.selector);
        }
      } else {
        el = await findElement(s.selector);
      }

      if (!el || cancelled) return;

      // scroll into view then compute rect
      el.scrollIntoView({ behavior: 'smooth', block: 'center', inline: 'center' });
      await new Promise(r => setTimeout(r, 200));

      // demo auto-fill / auto-click for specific steps
      try {
        if (s.autoFill) {
          const input = el.tagName.toLowerCase() === 'input' ? el : document.querySelector('.add-habit-input');
          if (input) {
            input.focus();
            input.value = s.autoFill;
            input.dispatchEvent(new Event('input', { bubbles: true }));
            await new Promise(r => setTimeout(r, 220));
          }
        }
        if (s.autoClick) {
          el.click();
          await new Promise(r => setTimeout(r, 380));
        }
        // for the Save step we may have filled the input; if the selector is button we can click it
        if (s.selector === '.add-habit-btn' && s.autoFill) {
          const btn = el;
          if (btn) {
            btn.click();
            await new Promise(r => setTimeout(r, 500));
          }
        }
      } catch (e) { /* ignore demo errors */ }

      const rect = el.getBoundingClientRect();
      const pointerBox = {
        top: rect.top + window.scrollY,
        left: rect.left + window.scrollX,
        width: rect.width,
        height: rect.height,
        placement: s.placement || 'top'
      };
      setPointer(pointerBox);
    })();

    return () => { cancelled = true; };
  }, [step, visible, token]);

  if (!visible) return null;

  const steps = interactiveSteps;

  const finish = async () => {
    try {
      localStorage.setItem('tutorialSeen', 'true');
      if (token) {
        try { await setTutorialSeen(token); } catch (e) {}
      }
    } catch (e) {}
    onClose();
  };

  const s = steps[step];

  return (
    <>
      <div className="tutorial-overlay" role="dialog" aria-modal="true">
        {pointer && (
          <>
            <div
              className="tutorial-highlight"
              style={{
                top: pointer.top - 8,
                left: pointer.left - 8,
                width: pointer.width + 16,
                height: pointer.height + 16
              }}
            />
            <div
              className={`tutorial-pointer tutorial-pointer-${pointer.placement}`}
              style={{
                top: (pointer.placement === 'bottom') ? (pointer.top + pointer.height + 12) : (pointer.top - 12),
                left: pointer.left + pointer.width / 2
              }}
            >
              <div className="tutorial-pointer-label">{s.title}</div>
            </div>
          </>
        )}

        <div
          ref={cardRef}
          className={`tutorial-card ${cardStyle ? 'tutorial-card--attached' : ''}`}
          role="document"
          style={cardStyle ? cardStyle : { zIndex: 13000 }}
        >
          <div className="tutorial-header">
            <div className="tutorial-step">Step {step + 1} / {steps.length}</div>
            <button className="tutorial-skip" onClick={finish}>Skip</button>
          </div>
          <h3 className="tutorial-title">{s.title}</h3>
          <p className="tutorial-body">{s.body}</p>
          <div className="tutorial-actions">
            <button disabled={step === 0} onClick={() => setStep(x => Math.max(0, x - 1))}>Back</button>
            {step < steps.length - 1 ? (
              <button onClick={() => setStep(x => Math.min(steps.length - 1, x + 1))}>Next</button>
            ) : (
              <button ref={finishBtnRef} className="finish" onClick={finish}>Finish</button>
            )}
          </div>
        </div>
      </div>
    </>
  );
}
