import React, { useState, useEffect, useContext } from 'react';
import { getHabits, addHabit, deleteHabit, completeHabit, getCompletions } from './api';
import { AuthContext } from './AuthContext';

// history helpers keyed by user id (fallback username); empty key uses generic
// so entries logged prior to knowing who is logged in can migrate later
function historyKey(userId, username) {
  if (userId) return 'habitHistory_id_' + userId;
  if (username) return 'habitHistory_' + username;
  return 'habitHistory';
}
function readHistory(userId, username) {
  const key = historyKey(userId, username);
  console.debug('[history] read', key, 'for', {userId, username});
  const json = localStorage.getItem(key);
  try { return json ? JSON.parse(json) : []; } catch { return []; }
}
function writeHistory(userId, username, arr) {
  const key = historyKey(userId, username);
  console.debug('[history] write', key, 'for', {userId, username}, 'len', arr.length);
  try {
    localStorage.setItem(key, JSON.stringify(arr));
  } catch (e) {
    // ignore write errors
  }
}
function pushHistory(userId, username, entry) {
  // Write to id-based key if we have it
  if (userId) {
    const arr = readHistory(userId, username);
    arr.unshift(entry);
    writeHistory(userId, username, arr);
  }
  // Always also write to username-based key (fallback/migration)
  if (username) {
    const arr2 = readHistory('', username);
    arr2.unshift(entry);
    writeHistory('', username, arr2);
  }
  // if neither known, use generic
  if (!userId && !username) {
    const arr3 = readHistory('', '');
    arr3.unshift(entry);
    writeHistory('', '', arr3);
  }
}

export default function Habits() {
  const { token, logout, userProfile } = useContext(AuthContext);
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState('');
  const [error, setError] = useState('');
  const [completions, setCompletions] = useState({});
  const today = new Date().toISOString().slice(0, 10);

  useEffect(() => {
    if (!token) return;
    getHabits(token).then(res => setHabits(res.data));
  }, [token]);

  const handleAdd = async () => {
    if (!newHabit) return;
    try {
      await addHabit(token, newHabit);
      pushHistory(userProfile.id, userProfile.username, `${new Date().toISOString()}: Added habit ‚Äú${newHabit}‚Äù`);
      setNewHabit('');
      const res = await getHabits(token);
      setHabits(res.data);
      window.dispatchEvent(new Event('habitUpdated'));
    } catch (e) {
      setError('Could not add habit');
    }
  };


  const handleDelete = async (id) => {
    const habit = habits.find(h => h.id === id);
    if (habit) pushHistory(userProfile.id, userProfile.username, `${new Date().toISOString()}: Deleted habit ‚Äú${habit.name}‚Äù`);
    await deleteHabit(token, id);
    setHabits(habits.filter(h => h.id !== id));
    window.dispatchEvent(new Event('habitUpdated'));
  };

  const handleComplete = async (id) => {
    await completeHabit(token, id, today);
    const habit = habits.find(h => h.id === id);
    if (habit) pushHistory(userProfile.id, userProfile.username, `${new Date().toISOString()}: Completed habit ‚Äú${habit.name}‚Äù`);
    setCompletions({ ...completions, [id]: true });
    // notify other components (e.g. profile drawer) about update
    window.dispatchEvent(new Event('habitUpdated'));
  };

  const fetchCompletions = async (id) => {
    const res = await getCompletions(token, id);
    setCompletions(c => ({ ...c, [id]: res.data.some(d => d.date === today) }));
  };

  useEffect(() => {
    habits.forEach(h => fetchCompletions(h.id));
    // eslint-disable-next-line
  }, [habits]);

  useEffect(() => {
    // whenever habits change we should refresh history too
    window.dispatchEvent(new Event('habitUpdated'));
  }, [habits]);

  const displayedHabits = habits;

  return (
    <div>
      <h2>Your Habits</h2>
      <button className="logout-btn" onClick={logout}>Logout</button>
      <div className="add-habit-row">
        <input
          className="add-habit-input"
          placeholder="New habit"
          value={newHabit}
          onChange={e => setNewHabit(e.target.value)}
          onKeyDown={e => { if (e.key === 'Enter') { e.preventDefault(); handleAdd(); } }}
        />
        <button className="add-habit-btn" onClick={handleAdd}>Add</button>
      </div>
      {error && <div className="error">{error}</div>}
      <ul>
        {displayedHabits.map(habit => (
          <li key={habit.id} className="habit-item">
            <span className="habit-name">{habit.name}</span>
            <div className="habit-actions">
              <button className="habit-delete-btn" onClick={() => handleDelete(habit.id)} title="Delete"><span role="img" aria-label="delete">üóëÔ∏è</span> Delete</button>
              <button className="habit-complete-btn" onClick={() => handleComplete(habit.id)} disabled={completions[habit.id]} title="Mark as done"><span role="img" aria-label="done">‚úÖ</span> Mark as done</button>
              {completions[habit.id] && <span className="habit-done" title="Completed">‚úîÔ∏è</span>}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
