import React from 'react';
import './ProfileDashboard.css';

// simple helper to determine badge state
function isBadgeUnlocked(badge, history) {
  // rudimentary rules for demo
  switch (badge.key) {
    case '7day':
      return history.length >= 7;
    case '30day':
      return history.length >= 30;
    default:
      return false;
  }
}

export default function ProfileDashboard({ visible, onClose, history, user }) {
  // sample badges. in real app these might come from props/server
  const badges = [
    { key: '7day', title: '7-Day Warrior' },
    { key: '30day', title: '30-Day Streak' },
    { key: 'pioneer', title: 'Pioneer' }
  ];

  return (
    <div className={`profile-drawer${visible ? ' open' : ''}`}>      
      <header className="profile-header">
        <div className="profile-userinfo">
          {user && user.avatar && <img src={user.avatar} alt="avatar" className="profile-avatar" />}
          <div className="profile-name">{user?.name || 'User'}</div>
        </div>
        <button className="profile-close" onClick={onClose} aria-label="Close profile">√ó</button>
      </header>
      <section className="profile-badges">
        <h3>Success Badges</h3>
        <div className="badge-grid">
          {badges.map(b => {
            const unlocked = isBadgeUnlocked(b, history);
            return (
              <div key={b.key} className={`badge${unlocked ? ' unlocked' : ''}`}>                
                <span className="badge-icon">{unlocked ? 'üèÖ' : 'üîí'}</span>
                <span className="badge-title">{b.title}</span>
              </div>
            );
          })}
        </div>
      </section>
      <section className="profile-history">
        <h3>History</h3>
        <div className="history-list">
          {history.length === 0 ? (
            <div className="history-empty">No entries yet.</div>
          ) : (
            history.map((h, idx) => (
              <div key={idx} className="history-item">
                {h}
              </div>
            ))
          )}
        </div>
      </section>
    </div>
  );
}
