import React, { useState, useEffect, useRef, useCallback } from 'react';

export default function Tutorial({ visible, onClose }) {
  const [step, setStep] = useState(0);
  const [highlight, setHighlight] = useState(null);
  const retryCountRef = useRef(0);

  const steps = [
    {
      title: 'Type a Habit',
      body: 'Type a habit in the input box below. Try "Brush your teeth" as a suggestion!',
      selector: '.add-habit-input',
      waitFor: 'input'
    },
    {
      title: 'Add the Habit',
      body: 'Click the "Add" button to save your habit.',
      selector: '.add-habit-btn',
      waitFor: 'habitAdded'
    },
    {
      title: 'Mark as Done',
      body: 'Click "Mark as done" to complete the habit for today.',
      selector: '.habit-complete-btn',
      waitFor: 'habitCompleted'
    },
    {
      title: 'Delete the Habit',
      body: 'Click "Delete" to remove the habit.',
      selector: '.habit-delete-btn',
      waitFor: 'habitDeleted'
    }
  ];

  const finish = useCallback(() => {
    try {
      localStorage.setItem('tutorialSeen', 'true');
      localStorage.removeItem('showGetStarted');
    } catch (e) {}
    onClose();
  }, [onClose]);

  const updateHighlight = useCallback((currentStep) => {
    const s = steps[currentStep];
    if (!s?.selector) {
      setHighlight(null);
      return;
    }
    const el = document.querySelector(s.selector);
    if (!el) {
      if (retryCountRef.current < 15) {
        retryCountRef.current++;
        setTimeout(() => updateHighlight(currentStep), 150);
      }
      return;
    }
    retryCountRef.current = 0;
    el.scrollIntoView({ behavior: 'smooth', block: 'center' });
    setTimeout(() => {
      const rect = el.getBoundingClientRect();
      setHighlight({ top: rect.top, left: rect.left, width: rect.width, height: rect.height });
    }, 250);
  }, [steps]);

  useEffect(() => {
    if (!visible) return;
    const s = steps[step];
    if (!s) return;

    let observer = null;
    let intervalId = null;
    let initialHabitCount = document.querySelectorAll('ul li').length;

    const advanceStep = () => {
      if (step < steps.length - 1) {
        setStep(prev => prev + 1);
      } else {
        finish();
      }
    };

    const checkCondition = () => {
      switch (s.waitFor) {
        case 'input': {
          const input = document.querySelector('.add-habit-input');
          if (input && input.value.trim().length > 0) { advanceStep(); return true; }
          break;
        }
        case 'habitAdded': {
          if (document.querySelectorAll('ul li').length > initialHabitCount) { advanceStep(); return true; }
          break;
        }
        case 'habitCompleted': {
          if (document.querySelector('.habit-done')) { advanceStep(); return true; }
          break;
        }
        case 'habitDeleted': {
          const count = document.querySelectorAll('ul li').length;
          if (count < initialHabitCount || (initialHabitCount > 0 && count === 0)) { finish(); return true; }
          break;
        }
        default: break;
      }
      return false;
    };

    observer = new MutationObserver(() => checkCondition());
    observer.observe(document.body, { childList: true, subtree: true, attributes: true });
    if (s.waitFor === 'input') intervalId = setInterval(checkCondition, 100);

    return () => {
      if (observer) observer.disconnect();
      if (intervalId) clearInterval(intervalId);
    };
  }, [visible, step, finish, steps]);

  useEffect(() => {
    if (visible) {
      retryCountRef.current = 0;
      updateHighlight(step);
    }
  }, [visible, step, updateHighlight]);

  useEffect(() => {
    if (visible) {
      setStep(0);
      setHighlight(null);
      retryCountRef.current = 0;
    }
  }, [visible]);

  if (!visible) return null;

  const s = steps[step];
  const cardWidth = 300;
  const cardHeight = 160;
  
  let cardStyle = { position: 'fixed', zIndex: 100001, width: cardWidth + 'px' };
  
  if (highlight) {
    const vw = window.innerWidth;
    const vh = window.innerHeight;
    const spaceBelow = vh - (highlight.top + highlight.height) - 20;
    
    if (spaceBelow >= cardHeight + 20) {
      cardStyle.top = highlight.top + highlight.height + 20;
      cardStyle.left = Math.max(20, Math.min(highlight.left, vw - cardWidth - 20));
    } else {
      cardStyle.top = Math.max(20, highlight.top - cardHeight - 20);
      cardStyle.left = Math.max(20, Math.min(highlight.left, vw - cardWidth - 20));
    }
  } else {
    cardStyle.top = '50%';
    cardStyle.left = '50%';
    cardStyle.transform = 'translate(-50%, -50%)';
  }

  return (
    <>
      <div className="tutorial-overlay-light" />
      {highlight && (
        <div
          className="tutorial-highlight"
          style={{
            top: highlight.top - 6,
            left: highlight.left - 6,
            width: highlight.width + 12,
            height: highlight.height + 12
          }}
        />
      )}
      <div className="tutorial-card tutorial-card--floating" role="dialog" style={cardStyle}>
        <div className="tutorial-header">
          <div className="tutorial-step">Step {step + 1} / {steps.length}</div>
          <button className="tutorial-skip" onClick={finish}>Skip</button>
        </div>
        <h3 className="tutorial-title">{s.title}</h3>
        <p className="tutorial-body">{s.body}</p>
      </div>
    </>
  );
}
