import React, { useState, useEffect, useContext } from 'react';
import { getHabits, addHabit, deleteHabit, completeHabit, getCompletions } from './api';
import { AuthContext } from './AuthContext';

export default function Habits() {
  const { token, logout } = useContext(AuthContext);
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState('');
  const [error, setError] = useState('');
  const [completions, setCompletions] = useState({});
  const today = new Date().toISOString().slice(0, 10);

  useEffect(() => {
    if (!token) return;
    getHabits(token).then(res => setHabits(res.data));
  }, [token]);

  const handleAdd = async () => {
    if (!newHabit) return;
    try {
      await addHabit(token, newHabit);
      setNewHabit('');
      const res = await getHabits(token);
      setHabits(res.data);
    } catch (e) {
      setError('Could not add habit');
    }
  };

  const handleDelete = async (id) => {
    await deleteHabit(token, id);
    setHabits(habits.filter(h => h.id !== id));
  };

  const handleComplete = async (id) => {
    await completeHabit(token, id, today);
    setCompletions({ ...completions, [id]: true });
    // notify other components (e.g. profile drawer) about update
    window.dispatchEvent(new Event('habitUpdated'));
  };

  const fetchCompletions = async (id) => {
    const res = await getCompletions(token, id);
    setCompletions(c => ({ ...c, [id]: res.data.some(d => d.date === today) }));
  };

  useEffect(() => {
    habits.forEach(h => fetchCompletions(h.id));
    // eslint-disable-next-line
  }, [habits]);

  return (
    <div>
      <h2>Your Habits</h2>
      <button className="logout-btn" onClick={logout}>Logout</button>
      <div className="add-habit-row">
        <input
          className="add-habit-input"
          placeholder="New habit"
          value={newHabit}
          onChange={e => setNewHabit(e.target.value)}
          onKeyDown={e => { if (e.key === 'Enter') { e.preventDefault(); handleAdd(); } }}
        />
        <button className="add-habit-btn" onClick={handleAdd}>Add</button>
      </div>
      {error && <div className="error">{error}</div>}
      <ul>
        {habits.map(habit => (
          <li key={habit.id} className="habit-item">
            <span className="habit-name">{habit.name}</span>
            <div className="habit-actions">
              <button className="habit-delete-btn" onClick={() => handleDelete(habit.id)} title="Delete"><span role="img" aria-label="delete">üóëÔ∏è</span> Delete</button>
              <button className="habit-complete-btn" onClick={() => handleComplete(habit.id)} disabled={completions[habit.id]} title="Mark as done"><span role="img" aria-label="done">‚úÖ</span> Mark as done</button>
              {completions[habit.id] && <span className="habit-done" title="Completed">‚úîÔ∏è</span>}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
